import classNames from "classnames"
import { useRef, useEffect, useState } from "react"
import { InputCheckboxComponent } from "./types"



// export const InputCheckbox: InputCheckboxComponent =({ id, checked, disabled, onChange }) => {
  export const InputCheckbox: InputCheckboxComponent = ({ id, disabled, onChange }) => {
 const { current: inputId } = useRef(`RampInputCheckbox-${id}`);


  const [checked, setChecked] = useState(() => {
    const storedValue = localStorage.getItem(inputId)
    return storedValue !== null ? JSON.parse(storedValue) : false
  })

  useEffect(() => {
    // Save checkbox state to localStorage whenever it changes
    localStorage.setItem(inputId, JSON.stringify(checked))
  }, [inputId, checked])


  return (
    <div className="RampInputCheckbox--container" data-testid={inputId}>
      <label
        htmlFor={inputId}
        className={classNames("RampInputCheckbox--label", {
          "RampInputCheckbox--label-checked": checked,
          "RampInputCheckbox--label-disabled": disabled,
        })}
      />
      <input
        id={inputId}
        type="checkbox"
        className="RampInputCheckbox--input"
        checked={checked}
        disabled={disabled}
        onChange={() => {
          setChecked((prevChecked: boolean) => !prevChecked)
          onChange(!checked)
        }}
      />
    </div>
  )
}
